import { useState } from "react";

const Lines = ({ line, handleDelete, handleEdit }) => {
  const [newLine, setNewLine] = useState(line.name);

  const handleChange = (e) => {
    e.preventDefault();
    setNewLine(e.target.value);
    //console.log(newLine);
  };

  return (
    <div className="Line">
      <input type="text" value={newLine} onChange={handleChange} />
      <button onClick={() => handleEdit(line, newLine)}>Edit </button>
      <button onClick={() => handleDelete(line.id)}>Delete </button>
    </div>
  );
};
export default Lines;
